import TechPage from './components/tech/tech';
import HomePage from './components/home/home';
// import { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faBars,faXmark } from '@fortawesome/free-solid-svg-icons'
import "./_common.scss";
import techJson from "./components/tech/tech-data.json"
import { BrowserRouter,Routes,Route,NavLink } from 'react-router-dom';


function App() {

  function handleClick(){
    let navBox=document.querySelector(".NavBarLogo nav");
    navBox.classList.toggle("displayOn")
    navBox.setAttribute("class"," animate__animated animate__bounce animate__delay-2s")
  }

  return (
    <div className="App">
      <BrowserRouter>
      <div className="NavBarLogo">
        <h1><span className="rotateLogo">The</span> Siren</h1>
        <FontAwesomeIcon icon={faBars} onClick={handleClick} className="hamburger-menu"/>
        <nav>
          <NavLink className="navlinks" to="/">Home</NavLink>
          <NavLink className="navlinks" to="/category/technology">Tech</NavLink>
          <NavLink className="navlinks" to="/category/sports">Sports</NavLink>
          <NavLink className="navlinks" to="/category/hollywood">Hollywood</NavLink>
          <NavLink className="navlinks" to="/category/food">Food</NavLink>
          <NavLink className="navlinks" to="/category/fitness">Fitness</NavLink>
        </nav>
        <hr />
      </div>
      <Routes>
        <Route path="/" element={<HomePage />}></Route>
        <Route path="/category/technology" element={<TechPage data={techJson}/>}></Route>
        <Route path="/category/sports"></Route>
        <Route path="/category/hollywood"></Route>
        <Route path="/category/food"></Route>
        <Route path="/category/fitness"></Route>
      </Routes>
      </BrowserRouter>
    </div>
  );
}

export default App;
